package Org.New.Interview;

public class VowelUsingSwitch {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		char ch='v';
		switch(ch)
		{
		case 'a':
		System.out.println("given is owel");
		case 'e':
		System.out.println("given is vowel");
		case 'i':
		System.out.println("gven is vowel");
		case 'o':
		System.out.println("given is vowel");
		case 'u':
			System.out.println("given is vowel");
			break;
			default:
				System.out.println("given is consonant");
				
		}

	}

}
